<?phpclass FactoryX_CustomReports_Block_Shoppedonce extends Mage_Adminhtml_Block_Template{    public function __construct()    {        parent::__construct();        $this->setTemplate('factoryx/customreports/shoppedonce.phtml');    }    public function _beforeToHtml()    {        $this->setChild('grid', $this->getLayout()->createBlock('customreports/shoppedonce_grid', 'customreports.grid'));        return $this;    }}