<?phpclass FactoryX_CustomReports_Block_Shoppedonce extends FactoryX_CustomReports_Block_Customreport{    public function __construct()    {        parent::__construct();        $this->setTemplate('factoryx/customreports/grid.phtml');		$this->setTitle('Custom Shopped Once And Never Again Report');    }    public function _beforeToHtml()    {        $this->setChild('grid', $this->getLayout()->createBlock('customreports/shoppedonce_grid', 'customreports.grid'));        return $this;    }}