INSERT INTO `cms_page` VALUES (1,'404 Not Found 1','one_column','Page keywords','Page description','no-route',NULL,'<div class=\"page-title\"><h1>Whoops, our bad...</h1></div>\r\n<dl>\r\n<dt>The page you requested was not found, and we have a fine guess why.</dt>\r\n<dd>\r\n<ul class=\"disc\">\r\n<li>If you typed the URL directly, please make sure the spelling is correct.</li>\r\n<li>If you clicked on a link to get here, the link is outdated.</li>\r\n</ul></dd>\r\n</dl>\r\n<dl>\r\n<dt>What can you do?</dt>\r\n<dd>Have no fear, help is near! There are many ways you can get back on track with {{config path=\"general/store_information/name\"}}.</dd>\r\n<dd>\r\n<ul class=\"disc\">\r\n<li><a href=\"#\" onclick=\"history.go(-1); return false;\">Go back</a> to the previous page.</li>\r\n<li>Use the search bar at the top of the page to search for your products.</li>\r\n<li>Follow these links to get you back on track!<br /><a href=\"{{store url=\"\"}}\">Store Home</a> <span class=\"separator\">|</span> <a href=\"{{store url=\"customer/account\"}}\">My Account</a></li></ul></dd></dl>\r\n','2013-12-12 23:18:45','2014-03-03 03:47:13',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(2,'Home page','homepage',NULL,NULL,'home',NULL,'<!--  -->','2013-12-12 23:18:45','2014-05-15 17:48:56',1,0,'<remove name=\"breadcrumbs\"/>\r\n<reference name=\"content\">\r\n    <block type=\"homepage/homepages\" name=\"content.homepage\" before=\"cms.wrapper\" />\r\n</reference>',NULL,NULL,NULL,NULL,NULL,'','',0),(3,'Our Story','one_column',NULL,NULL,'our-story',NULL,'<div class=\"row\">\r\n<div class=\"span8\">\r\n<h2>Our Story</h2>\r\n<p>\r\nPrincess Highway lovingly creates a range of vintage inspired clothing, with an infusion of our favourite colours, nostalgic prints and original sweaters and cardis for every girl.\r\n\r\nPrincess Highway doesn\'t follow trends, rather, it maintains its aesthetic from season to season, making it timeless, reliable and gorgeous for its customers.\r\n\r\nBy mixing new seasonal pieces with wardrobe favourites, Princess Highway girls can create their own jolly cute look.  </p>\r\n\r\n\r\n</div>\r\n</div>','2013-12-12 23:18:45','2014-05-26 04:50:36',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(5,'Enable Cookies','one_column',NULL,NULL,'enable-cookies',NULL,'<div class=\"std\">\r\n    <ul class=\"messages\">\r\n        <li class=\"notice-msg\">\r\n            <ul>\r\n                <li>Please enable cookies in your web browser to continue.</li>\r\n            </ul>\r\n        </li>\r\n    </ul>\r\n    <div class=\"page-title\">\r\n        <h1><a name=\"top\"></a>What are Cookies?</h1>\r\n    </div>\r\n    <p>Cookies are short pieces of data that are sent to your computer when you visit a website. On later visits, this data is then returned to that website. Cookies allow us to recognize you automatically whenever you visit our site so that we can personalize your experience and provide you with better service. We also use cookies (and similar browser data, such as Flash cookies) for fraud prevention and other purposes. If your web browser is set to refuse cookies from our website, you will not be able to complete a purchase or take advantage of certain features of our website, such as storing items in your Shopping Cart or receiving personalized recommendations. As a result, we strongly encourage you to configure your web browser to accept cookies from our website.</p>\r\n    <h2 class=\"subtitle\">Enabling Cookies</h2>\r\n    <ul class=\"disc\">\r\n        <li><a href=\"#ie7\">Internet Explorer 7.x</a></li>\r\n        <li><a href=\"#ie6\">Internet Explorer 6.x</a></li>\r\n        <li><a href=\"#firefox\">Mozilla/Firefox</a></li>\r\n        <li><a href=\"#opera\">Opera 7.x</a></li>\r\n    </ul>\r\n    <h3><a name=\"ie7\"></a>Internet Explorer 7.x</h3>\r\n    <ol>\r\n        <li>\r\n            <p>Start Internet Explorer</p>\r\n        </li>\r\n        <li>\r\n            <p>Under the <strong>Tools</strong> menu, click <strong>Internet Options</strong></p>\r\n            <p><img src=\"{{skin url=\"images/cookies/ie7-1.gif\"}}\" alt=\"\" /></p>\r\n        </li>\r\n        <li>\r\n            <p>Click the <strong>Privacy</strong> tab</p>\r\n            <p><img src=\"{{skin url=\"images/cookies/ie7-2.gif\"}}\" alt=\"\" /></p>\r\n        </li>\r\n        <li>\r\n            <p>Click the <strong>Advanced</strong> button</p>\r\n            <p><img src=\"{{skin url=\"images/cookies/ie7-3.gif\"}}\" alt=\"\" /></p>\r\n        </li>\r\n        <li>\r\n            <p>Put a check mark in the box for <strong>Override Automatic Cookie Handling</strong>, put another check mark in the <strong>Always accept session cookies </strong>box</p>\r\n            <p><img src=\"{{skin url=\"images/cookies/ie7-4.gif\"}}\" alt=\"\" /></p>\r\n        </li>\r\n        <li>\r\n            <p>Click <strong>OK</strong></p>\r\n            <p><img src=\"{{skin url=\"images/cookies/ie7-5.gif\"}}\" alt=\"\" /></p>\r\n        </li>\r\n        <li>\r\n            <p>Click <strong>OK</strong></p>\r\n            <p><img src=\"{{skin url=\"images/cookies/ie7-6.gif\"}}\" alt=\"\" /></p>\r\n        </li>\r\n        <li>\r\n            <p>Restart Internet Explore</p>\r\n        </li>\r\n    </ol>\r\n    <p class=\"a-top\"><a href=\"#top\">Back to Top</a></p>\r\n    <h3><a name=\"ie6\"></a>Internet Explorer 6.x</h3>\r\n    <ol>\r\n        <li>\r\n            <p>Select <strong>Internet Options</strong> from the Tools menu</p>\r\n            <p><img src=\"{{skin url=\"images/cookies/ie6-1.gif\"}}\" alt=\"\" /></p>\r\n        </li>\r\n        <li>\r\n            <p>Click on the <strong>Privacy</strong> tab</p>\r\n        </li>\r\n        <li>\r\n            <p>Click the <strong>Default</strong> button (or manually slide the bar down to <strong>Medium</strong>) under <strong>Settings</strong>. Click <strong>OK</strong></p>\r\n            <p><img src=\"{{skin url=\"images/cookies/ie6-2.gif\"}}\" alt=\"\" /></p>\r\n        </li>\r\n    </ol>\r\n    <p class=\"a-top\"><a href=\"#top\">Back to Top</a></p>\r\n    <h3><a name=\"firefox\"></a>Mozilla/Firefox</h3>\r\n    <ol>\r\n        <li>\r\n            <p>Click on the <strong>Tools</strong>-menu in Mozilla</p>\r\n        </li>\r\n        <li>\r\n            <p>Click on the <strong>Options...</strong> item in the menu - a new window open</p>\r\n        </li>\r\n        <li>\r\n            <p>Click on the <strong>Privacy</strong> selection in the left part of the window. (See image below)</p>\r\n            <p><img src=\"{{skin url=\"images/cookies/firefox.png\"}}\" alt=\"\" /></p>\r\n        </li>\r\n        <li>\r\n            <p>Expand the <strong>Cookies</strong> section</p>\r\n        </li>\r\n        <li>\r\n            <p>Check the <strong>Enable cookies</strong> and <strong>Accept cookies normally</strong> checkboxes</p>\r\n        </li>\r\n        <li>\r\n            <p>Save changes by clicking <strong>Ok</strong>.</p>\r\n        </li>\r\n    </ol>\r\n    <p class=\"a-top\"><a href=\"#top\">Back to Top</a></p>\r\n    <h3><a name=\"opera\"></a>Opera 7.x</h3>\r\n    <ol>\r\n        <li>\r\n            <p>Click on the <strong>Tools</strong> menu in Opera</p>\r\n        </li>\r\n        <li>\r\n            <p>Click on the <strong>Preferences...</strong> item in the menu - a new window open</p>\r\n        </li>\r\n        <li>\r\n            <p>Click on the <strong>Privacy</strong> selection near the bottom left of the window. (See image below)</p>\r\n            <p><img src=\"{{skin url=\"images/cookies/opera.png\"}}\" alt=\"\" /></p>\r\n        </li>\r\n        <li>\r\n            <p>The <strong>Enable cookies</strong> checkbox must be checked, and <strong>Accept all cookies</strong> should be selected in the &quot;<strong>Normal cookies</strong>&quot; drop-down</p>\r\n        </li>\r\n        <li>\r\n            <p>Save changes by clicking <strong>Ok</strong></p>\r\n        </li>\r\n    </ol>\r\n    <p class=\"a-top\"><a href=\"#top\">Back to Top</a></p>\r\n</div>\r\n','2013-12-12 23:18:45','2013-12-12 23:18:45',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(6,'Privacy Policy','one_column',NULL,NULL,'privacy-policy-cookie-restriction-mode','Privacy Policy','<p style=\"color: #ff0000; font-weight: bold; font-size: 13px\">\n    Please replace this text with you Privacy Policy.\n    Please add any additional cookies your website uses below (e.g., Google Analytics)\n</p>\n<p>\n    This privacy policy sets out how {{config path=\"general/store_information/name\"}} uses and protects any information\n    that you give {{config path=\"general/store_information/name\"}} when you use this website.\n    {{config path=\"general/store_information/name\"}} is committed to ensuring that your privacy is protected.\n    Should we ask you to provide certain information by which you can be identified when using this website,\n    then you can be assured that it will only be used in accordance with this privacy statement.\n    {{config path=\"general/store_information/name\"}} may change this policy from time to time by updating this page.\n    You should check this page from time to time to ensure that you are happy with any changes.\n</p>\n<h2>What we collect</h2>\n<p>We may collect the following information:</p>\n<ul>\n    <li>name</li>\n    <li>contact information including email address</li>\n    <li>demographic information such as postcode, preferences and interests</li>\n    <li>other information relevant to customer surveys and/or offers</li>\n</ul>\n<p>\n    For the exhaustive list of cookies we collect see the <a href=\"#list\">List of cookies we collect</a> section.\n</p>\n<h2>What we do with the information we gather</h2>\n<p>\n    We require this information to understand your needs and provide you with a better service,\n    and in particular for the following reasons:\n</p>\n<ul>\n    <li>Internal record keeping.</li>\n    <li>We may use the information to improve our products and services.</li>\n    <li>\n        We may periodically send promotional emails about new products, special offers or other information which we\n        think you may find interesting using the email address which you have provided.\n    </li>\n    <li>\n        From time to time, we may also use your information to contact you for market research purposes.\n        We may contact you by email, phone, fax or mail. We may use the information to customise the website\n        according to your interests.\n    </li>\n</ul>\n<h2>Security</h2>\n<p>\n    We are committed to ensuring that your information is secure. In order to prevent unauthorised access or disclosure,\n    we have put in place suitable physical, electronic and managerial procedures to safeguard and secure\n    the information we collect online.\n</p>\n<h2>How we use cookies</h2>\n<p>\n    A cookie is a small file which asks permission to be placed on your computer\'s hard drive.\n    Once you agree, the file is added and the cookie helps analyse web traffic or lets you know when you visit\n    a particular site. Cookies allow web applications to respond to you as an individual. The web application\n    can tailor its operations to your needs, likes and dislikes by gathering and remembering information about\n    your preferences.\n</p>\n<p>\n    We use traffic log cookies to identify which pages are being used. This helps us analyse data about web page traffic\n    and improve our website in order to tailor it to customer needs. We only use this information for statistical\n    analysis purposes and then the data is removed from the system.\n</p>\n<p>\n    Overall, cookies help us provide you with a better website, by enabling us to monitor which pages you find useful\n    and which you do not. A cookie in no way gives us access to your computer or any information about you,\n    other than the data you choose to share with us. You can choose to accept or decline cookies.\n    Most web browsers automatically accept cookies, but you can usually modify your browser setting\n    to decline cookies if you prefer. This may prevent you from taking full advantage of the website.\n</p>\n<h2>Links to other websites</h2>\n<p>\n    Our website may contain links to other websites of interest. However, once you have used these links\n    to leave our site, you should note that we do not have any control over that other website.\n    Therefore, we cannot be responsible for the protection and privacy of any information which you provide whilst\n    visiting such sites and such sites are not governed by this privacy statement.\n    You should exercise caution and look at the privacy statement applicable to the website in question.\n</p>\n<h2>Controlling your personal information</h2>\n<p>You may choose to restrict the collection or use of your personal information in the following ways:</p>\n<ul>\n    <li>\n        whenever you are asked to fill in a form on the website, look for the box that you can click to indicate\n        that you do not want the information to be used by anybody for direct marketing purposes\n    </li>\n    <li>\n        if you have previously agreed to us using your personal information for direct marketing purposes,\n        you may change your mind at any time by writing to or emailing us at\n        {{config path=\"trans_email/ident_general/email\"}}\n    </li>\n</ul>\n<p>\n    We will not sell, distribute or lease your personal information to third parties unless we have your permission\n    or are required by law to do so. We may use your personal information to send you promotional information\n    about third parties which we think you may find interesting if you tell us that you wish this to happen.\n</p>\n<p>\n    You may request details of personal information which we hold about you under the Data Protection Act 1998.\n    A small fee will be payable. If you would like a copy of the information held on you please write to\n    {{config path=\"general/store_information/address\"}}.\n</p>\n<p>\n    If you believe that any information we are holding on you is incorrect or incomplete,\n    please write to or email us as soon as possible, at the above address.\n    We will promptly correct any information found to be incorrect.\n</p>\n<h2><a name=\"list\"></a>List of cookies we collect</h2>\n<p>The table below lists the cookies we collect and what information they store.</p>\n<table class=\"data-table\">\n    <thead>\n        <tr>\n            <th>COOKIE name</th>\n            <th>COOKIE Description</th>\n        </tr>\n    </thead>\n    <tbody>\n        <tr>\n            <th>CART</th>\n            <td>The association with your shopping cart.</td>\n        </tr>\n        <tr>\n            <th>CATEGORY_INFO</th>\n            <td>Stores the category info on the page, that allows to display pages more quickly.</td>\n        </tr>\n        <tr>\n            <th>COMPARE</th>\n            <td>The items that you have in the Compare Products list.</td>\n        </tr>\n        <tr>\n            <th>CURRENCY</th>\n            <td>Your preferred currency</td>\n        </tr>\n        <tr>\n            <th>CUSTOMER</th>\n            <td>An encrypted version of your customer id with the store.</td>\n        </tr>\n        <tr>\n            <th>CUSTOMER_AUTH</th>\n            <td>An indicator if you are currently logged into the store.</td>\n        </tr>\n        <tr>\n            <th>CUSTOMER_INFO</th>\n            <td>An encrypted version of the customer group you belong to.</td>\n        </tr>\n        <tr>\n            <th>CUSTOMER_SEGMENT_IDS</th>\n            <td>Stores the Customer Segment ID</td>\n        </tr>\n        <tr>\n            <th>EXTERNAL_NO_CACHE</th>\n            <td>A flag, which indicates whether caching is disabled or not.</td>\n        </tr>\n        <tr>\n            <th>FRONTEND</th>\n            <td>You sesssion ID on the server.</td>\n        </tr>\n        <tr>\n            <th>GUEST-VIEW</th>\n            <td>Allows guests to edit their orders.</td>\n        </tr>\n        <tr>\n            <th>LAST_CATEGORY</th>\n            <td>The last category you visited.</td>\n        </tr>\n        <tr>\n            <th>LAST_PRODUCT</th>\n            <td>The most recent product you have viewed.</td>\n        </tr>\n        <tr>\n            <th>NEWMESSAGE</th>\n            <td>Indicates whether a new message has been received.</td>\n        </tr>\n        <tr>\n            <th>NO_CACHE</th>\n            <td>Indicates whether it is allowed to use cache.</td>\n        </tr>\n        <tr>\n            <th>PERSISTENT_SHOPPING_CART</th>\n            <td>A link to information about your cart and viewing history if you have asked the site.</td>\n        </tr>\n        <tr>\n            <th>POLL</th>\n            <td>The ID of any polls you have recently voted in.</td>\n        </tr>\n        <tr>\n            <th>POLLN</th>\n            <td>Information on what polls you have voted on.</td>\n        </tr>\n        <tr>\n            <th>RECENTLYCOMPARED</th>\n            <td>The items that you have recently compared.            </td>\n        </tr>\n        <tr>\n            <th>STF</th>\n            <td>Information on products you have emailed to friends.</td>\n        </tr>\n        <tr>\n            <th>STORE</th>\n            <td>The store view or language you have selected.</td>\n        </tr>\n        <tr>\n            <th>USER_ALLOWED_SAVE_COOKIE</th>\n            <td>Indicates whether a customer allowed to use cookies.</td>\n        </tr>\n        <tr>\n            <th>VIEWED_PRODUCT_IDS</th>\n            <td>The products that you have recently viewed.</td>\n        </tr>\n        <tr>\n            <th>WISHLIST</th>\n            <td>An encrypted list of products added to your Wishlist.</td>\n        </tr>\n        <tr>\n            <th>WISHLIST_CNT</th>\n            <td>The number of items in your Wishlist.</td>\n        </tr>\n    </tbody>\n</table>','2013-12-12 23:18:45','2013-12-12 23:18:45',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(7,'Jobs View','two_columns_right','','','jobsview.html','','<p>{{block type=\"core/template\" name=\"Jobsview\" template=\"Jobs/Jobsview.phtml\"}}</p>','2007-08-30 04:01:18','2011-07-14 22:31:12',0,0,'','','','',NULL,NULL,'','',0),(8,'Jobs View Details','two_columns_right','','','jobsviewdetails.html','','<p>{{block type=\"core/template\" name=\"Jobsviewdetails\" template=\"Jobs/Jobsviewdetails.phtml\"}}</p>','2011-07-14 22:30:19','2011-07-14 22:48:34',0,0,'','','','',NULL,NULL,'','',0),(9,'Online From','two_columns_right','','','onlineform.html','','<p>{{block type=\"core/template\" name=\"onlineform\" template=\"Jobs/online/onlineform.phtml\"}}</p>','2011-07-14 23:55:31','2011-07-14 23:57:47',0,0,'','','','',NULL,NULL,'','',0),(10,'Returns And Exchanges','one_column',NULL,NULL,'returns-exchanges',NULL,'<div class=\"row\">\r\n<div class=\"span12\" id=\"returns\">\r\n\r\n<p> The {{config path=\"general/store_information/name\"}} store is happy to offer customers a return or an exchange for items purchased online. Returns or exchanges are accepted on goods within 21 days from the date of purchase. Items must be in original condition and all tags must remain in place. </p>\r\n<li> If you wish to return or exchange your item/s please choose from the two options below. </li>\r\n<li> Please note there is no return on sale items, however we are happy to exchange sale items. </li>\r\n<br>\r\n<h2>Return by Post</h2>\r\n<p> All deliveries will come with an Online Store Returns Form. This must be completed and sent back with your return, along with your receipt to: </p>\r\n<p class=\"returns-address\">\r\n<b>{{config path=\"general/store_information/name\"}} Returns/Exchanges</b>\r\n<br>\r\n61 Church Street\r\n<br>\r\nAbbotsford 3067\r\n<br>\r\nVictoria, Austalia\r\n<br>\r\n</p>\r\n<p> Once the item/s have been received and reviewed, you will either receive your new items (for exchange), or a refund for the cost of the purchase (excluding original shipping cost). Refunds will be issued directly to the credit card used for the purchase. Email notification will be sent once the return has been processed. </p>\r\n<p> Please note the online boutique can only exchange items for a different colour or size. If you wish to exchange you item/s for a different style, we recommend you visit one of our beautiful boutiques. </p>\r\n<h2>Return to Store</h2>\r\n<p> {{config path=\"general/store_information/name\"}} is happy to exchange online purchases for \'change of mind\' reasons at any boutique within 21 days of purchase. The online purchase receipt as well as the purchase credit card are required when making an exchange. Please note that {{config path=\"general/store_information/name\"}} boutiques can only exchange items purchased online. Boutiques are unable to issue refunds. These can only be processed by the online store. </p>\r\n<p>\r\n<h2>Faulty Items</h2>\r\nIf your item is faulty please contact us on {{config path=\"general/store_information/phone\"}} or email us at\r\n<a href=\"mailto:{{config path=\"trans_email/ident_general/email\"}}\">{{config path=\"trans_email/ident_general/email\"}}</a>\r\nand we will send a pre-paid post satchel for you to return the faulty item. Once the item has been received we will happily issue you with a full refund.\r\n</p>\r\n\r\n<h2>Colours and Sizes</h2>\r\nPlease note that colours may vary slightly from those displayed by your monitor.\r\nIf you need help establishing what size you are, please refer to our sizing chart.\r\n <br><br>\r\n<h2>Sold Out Items</h2>\r\nIf an item you have ordered is out of stock online, we will attempt to locate your item at another store, and dispatch to you as soon as the item becomes available. We will notify you if the item has sold out across all stores.\r\nIf a specific size or colour is not listed it is currently out of stock or sold out.\r\n\r\n</div>\r\n</div>','2013-12-23 04:11:43','2014-05-23 01:09:23',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(11,'Shipping and Delivery','one_column',NULL,NULL,'shipping-delivery',NULL,'<div class=\"row\">\r\n	<div class=\"span12\" id=\"shipping\">\r\n\r\n		<h2>Domestic shipping</h2>\r\n		<p>We offer free E-parcel Australia Post shipping on all domestic orders. All orders totalling $50 and above will receive complimentary E-parcel Express post delivery. The Express post option is available on orders below $50 for a flat rate of $7.50.</p>\r\n\r\n		<h2>Dispatch and Delivery Times</h2>\r\n		<p>Orders are dispatched from Melbourne - any limited sale/promotional stock may be sourced and shipped from our boutiques nationwide too. We aim to dispatch orders within 3-4 business days after purchase. Some items are of limited stock and may take up to 10 business days to source. In these cases orders may be dispatched in more than one shipment.  Customers will be notified of any dispatch delays..</p>\r\n\r\n	<div style=\'font-size:12px; border:1px solid red; padding:5px; margin-top:5px;\'>\r\n  			Please note that Once your order is placed, we cannot cancel or amend it as we begin sourcing/packing right away. During SALE periods, orders may also take an extra 3-4 days to dispatch on top of normal dispatch times.</div>\r\n		<br>\r\n<p>\r\n		\r\nAfter dispatch, Standard delivery to metropolitan areas will usually arrive within 2 business days and between 2 to 5 business days for regional areas. Express shipping to metropolitan* areas will usually arrive within 1 business day of dispatch. *Please refer to the Australia Post website for further information regarding delivery times.\r\n</p>\r\n\r\n		<h2>International shipping</h2>\r\n		<p>Please note, we do not ship to the following countries: Ghana, Malaysia, Nigeria, Pakistan & South Africa.</p>\r\n\r\n		<p>For International orders we charge a flat rate of $25 and ship via DHL. All orders are dispatched from Melbourne. We aim to dispatch orders within 3-4 business days after purchase. Some items are of limited stock and may take up to 10 business days to source.</p>\r\n		\r\n		<p>All products are sold on a Delivery Duty unpaid basis. The recipient may have to pay import duty or a formal customs entry fee prior to or on receipt. Additional taxes, fees or levies may apply according to local legislation and customers are required to check these details before placing any orders for international shipping. Please note that many countries have specific import restrictions on certain products or materials and it is the responsibility of customers to check local customs regulations before placing an order for shipping outside Australia. Factory X Pty. Ltd. does not offer any assistance in this process.\r\n</p>\r\n\r\n		<table class=\"table table-bordered ordertracking\">\r\n		     <thead>\r\n		          <tr>\r\n		               <th class=\"title\" colspan=\"3\">Order tracking</th>\r\n		          </tr>\r\n		     </thead>\r\n		     <tbody>\r\n		          <tr>\r\n		               <td class=\"description\" colspan=\"3\">To track your orders please login to your account or check your shipping email and visit the website of the corresponding courier below.</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"logo\"><img src=\"/skin/frontend/default/princess/images/media/shipping/austpost.png\" title=\"Australia Post Parcel Tracking\" alt=\"Australia Post Parcel Tracking\" width=\"100\" /></td>\r\n		               <td class=\"title\">Shipped with AustPost</td>\r\n		               <td class=\"link\"><a href=\"http://auspost.com.au/track/\" title=\"Australia Post Parcel Tracking\">http://auspost.com.au/track/</a></td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"logo\"><img src=\"/skin/frontend/default/princess/images/media/shipping/dhl.gif\" title=\"DHL Parcel Tracking\" alt=\"DHL Parcel Tracking\" width=\"100\" /></td>\r\n		               <td class=\"title\">Shipped with DHL</td>\r\n		               <td class=\"link\"><a href=\"http://www.dhl.com.au/\" title=\"DHL Parcel Tracking\">http://www.dhl.com.au/</a></td>\r\n		          </tr>\r\n		    </tbody>     \r\n		</table>\r\n\r\n\r\n		<h2>Delivery address</h2>\r\n		<p>{{config path=\"general/store_information/name\"}} Online recommends customers provide a secure delivery address, for example your place of employment, as we are not responsible for stolen parcels.</p>\r\n\r\n	</div>\r\n</div>','2013-12-23 04:12:56','2014-05-23 00:51:42',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(12,'Terms and Conditions','one_column',NULL,NULL,'terms-conditions.html',NULL,'<div class=\"row\">\r\n	<div class=\"span8\" id=\"termsandconditions\">\r\n\r\n		<h2>Privacy & security</h2>\r\n		<p>{{config path=\"general/store_information/name\"}} recognise and agree that the privacy of all personal information is important to individuals. All personal information collected to process and fulfill your order is to be minimal, private, correct, safe and most importantly in the control of the individual. We do not share any credit card information with any third parties, other than for the purpose of processing payments for the product you have ordered. We will only ask for and use relevant personal information necessary to conduct our business. All information collected for {{config path=\"general/store_information/name\"}} is for the sole purpose and use of the brand only.</p>\r\n\r\n		<h2>Returns and exchanges</h2>\r\n		<p><a href=\"returns-exchanges.html\" title=\"Returns and exchanges\">Click here</a> for our returns and exchanges policy.</p>\r\n\r\n		<h2>Colours & sizes</h2>\r\n		<p>Please note that colours may vary slightly from those displayed by your monitor.</p>\r\n		<p>If you need help establishing what size you are at {{config path=\"general/store_information/name\"}} please refer to our sizing chart.</p>\r\n\r\n		<h2>Sold out items</h2>\r\n		<p>If an item you have ordered is out of stock online, we will attempt to locate your item at another store, and dispatch to you as soon as the item becomes available. we will notify you if the item has sold out across all stores.</p>\r\n		<p>If a specific size or colour is not listed it is currently out of stock or sold out.</p>\r\n\r\n		<h2>Payment Options and Disputes</h2>\r\n		<p>Presently we accept payments from Paypal, Visa, Mastercard and Amex.<br><img alt=\"\" src=\"/skin/frontend/default/princess/images/payment_methods.png\"></p>\r\n		<p>If you have any queries or disputes with payments and or refunds please contact the relevant financial institution.</p>\r\n		<div class=\"address\">\r\n		<p>\r\n		<strong>\r\n				<a target=\"_blank\" href=\"https://www.paypal.com/au/contactus\">PayPal Support</a>\r\n		</strong>\r\n		<br>\r\n		Ph. 1800-073-263\r\n		<br>\r\n		Monday - Saturday 6 am - 10 pm AEST.\r\n		</p>\r\n		</div>\r\n		<p>For all orders over $1500, please call {{config path=\"general/store_information/phone\"}} to confirm stock fulfillment & payment method (bank transfer preferred).</p>\r\n\r\n		<h2>Gift Certificates & Coupon Codes</h2>\r\n		<p>Both online & in-store gift certificates can be purchased <a href=\"/gift-cards.html\">here.</a></p>\r\n		<p>Cards and codes are only valid for a 12 month period. You will be notified via email before they expire.</p>\r\n		<p>If you want to transfer online credit to an in-store card or vice versa then please contact use <a href=\"mailto:{{config path=\"trans_email/ident_general/email\"}}\">{{config path=\"trans_email/ident_general/email\"}}</a> with the amounts and relevant codes.</p>\r\n\r\n		<h3>Gift Certificate Usage Policy</h3>\r\n		<p>Lost or stolen cards/codes will not be replaced or refunded.</p>\r\n		<p>Any purchase made using an online gift certifcate will only ever be refunded back into the original gift certificate. If an alternate payment method was used to pay the difference, then the difference is the only amount that can ever be refunded back into that payment methods source account. </p>\r\n\r\n		<h2>Careers</h2>\r\n\r\n		<p>If you are passionate about fashion, love giving exceptional customer service and have an amazing styling ability then we would love to hear from you. Our success is built on the energy and talent of our staff.</p>\r\n		<p>If you are interested in joining the {{config path=\"general/store_information/name\"}} email your CV to <a href=\"mailto:{{config path=\"trans_email/ident_general/email\"}}\">{{config path=\"trans_email/ident_general/email\"}}</a> with your state in the subject title.</p>\r\n\r\n		<h2>Contact Us</h2>\r\n\r\n		{{config path=\"general/store_information/address\"}}\r\n\r\n		{{config path=\"general/store_information/name\"}} Head Office is open Monday to Friday, 9am to 8pm - Phone: {{config path=\"general/store_information/phone\"}}\r\n		{{config path=\"general/store_information/name\"}} welcomes any feedback or enquires that you may have. Please feel free to email us at <a href=\"mailto:{{config path=\"trans_email/ident_general/email\"}}\">{{config path=\"trans_email/ident_general/email\"}}</a>\r\n\r\n		<h2>Cookies</h2>\r\n\r\n		<p>To flush your browser cookies, please follow the steps on the following website:   <a href=\"http://www.wikihow.com/Clear-Your-Browser%27s-Cookies\">http://www.wikihow.com/Clear-Your-Browser%27s-Cookies</a></p>\r\n	</div>\r\n</div>\r\n','2013-12-23 04:16:19','2014-02-06 00:04:06',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(14,'Size Chart','one_column',NULL,NULL,'size-chart.html',NULL,'<div class=\"row\" id=\"sizecharts\">\r\n	<div class=\"span6\">\r\n\r\n		<table class=\"table table-bordered sizechart\">\r\n		     <thead>\r\n		          <tr>\r\n		               <th class=\"title\" colspan=\"6\">Body Measurments (cm)</th>\r\n		          </tr>\r\n		     </thead>\r\n		     <tbody>\r\n		          <tr>\r\n		               <td class=\"legend\">Size</td>\r\n		               <td class=\"sizing\">6</td>\r\n		               <td class=\"sizing\">8</td>\r\n		               <td class=\"sizing\">10</td>\r\n		               <td class=\"sizing\">12</td>\r\n		               <td class=\"sizing\">14</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">&nbsp;</td>\r\n		               <td class=\"sizing\">XXS</td>\r\n		               <td class=\"sizing\">XS</td>\r\n		               <td class=\"sizing\">S</td>\r\n		               <td class=\"sizing\">M</td>\r\n		               <td class=\"sizing\">L</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">Bust</td>\r\n		               <td class=\"sizing\">80</td>\r\n		               <td class=\"sizing\">85</td>\r\n		               <td class=\"sizing\">90</td>\r\n		               <td class=\"sizing\">95</td>\r\n		               <td class=\"sizing\">100</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">Waist</td>\r\n		               <td class=\"sizing\">61</td>\r\n		               <td class=\"sizing\">66</td>\r\n		               <td class=\"sizing\">71</td>\r\n		               <td class=\"sizing\">76</td>\r\n		               <td class=\"sizing\">81</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">Hip</td>\r\n		               <td class=\"sizing\">89</td>\r\n		               <td class=\"sizing\">94</td>\r\n		               <td class=\"sizing\">99</td>\r\n		               <td class=\"sizing\">104</td>\r\n		               <td class=\"sizing\">109</td>\r\n		          </tr>\r\n		    </tbody>     \r\n		</table>\r\n\r\n		<table class=\"table table-bordered sizechart\">\r\n		     <thead>\r\n		          <tr>\r\n		               <th class=\"title\" colspan=\"7\">Footwear</th>\r\n		          </tr>\r\n		     </thead>\r\n		     <tbody>\r\n		          <tr>\r\n		               <td class=\"legend\">Australia</td>\r\n		               <td class=\"sizing\">5</td>\r\n		               <td class=\"sizing\">6</td>\r\n		               <td class=\"sizing\">7</td>\r\n		               <td class=\"sizing\">8</td>\r\n		               <td class=\"sizing\">9</td>\r\n		               <td class=\"sizing\">10</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">UK</td>\r\n		               <td class=\"sizing\">-</td>\r\n		               <td class=\"sizing\">4</td>\r\n		               <td class=\"sizing\">5</td>\r\n		               <td class=\"sizing\">6</td>\r\n		               <td class=\"sizing\">7</td>\r\n		               <td class=\"sizing\">8</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">Europe</td>\r\n		               <td class=\"sizing\">36</td>\r\n		               <td class=\"sizing\">37</td>\r\n		               <td class=\"sizing\">38</td>\r\n		               <td class=\"sizing\">39</td>\r\n		               <td class=\"sizing\">40</td>\r\n		               <td class=\"sizing\">41</td>\r\n		          </tr>\r\n		    </tbody>     \r\n		</table>\r\n\r\n\r\n		<table class=\"table table-bordered sizechart\">\r\n		     <thead>\r\n		          <tr>\r\n		               <th class=\"title\" colspan=\"4\">Belts</th>\r\n		          </tr>\r\n		     </thead>\r\n		     <tbody>\r\n		          <tr>\r\n		               <td class=\"legend\">Belt</td>\r\n		               <td class=\"sizing\">S</td>\r\n		               <td class=\"sizing\">M</td>\r\n		               <td class=\"sizing\">L</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">Size</td>\r\n		               <td class=\"sizing\">71cm</td>\r\n		               <td class=\"sizing\">83.5cm</td>\r\n		               <td class=\"sizing\">96cm</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">Waist</td>\r\n		               <td class=\"sizing\" colspan=\"3\">See image to the right for belt size measurements.</td>\r\n		          </tr>\r\n		    </tbody>     \r\n		</table>\r\n\r\n	</div>\r\n\r\n\r\n\r\n\r\n	<div class=\"span6\">\r\n\r\n		<table class=\"table table-bordered sizechart\">\r\n		     <thead>\r\n		          <tr>\r\n		               <th class=\"title\" colspan=\"2\">Measurement Tips</th>\r\n		          </tr>\r\n		     </thead>\r\n		     <tbody>\r\n		          <tr>\r\n		               <td class=\"legend\">Bust</td>\r\n		               <td class=\"description\">With your arms down, measure around the fullest part of your bust.</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">Waist</td>\r\n		               <td class=\"description\">Measure around your waistline keeping the measuring tape comfortably loose.</td>\r\n		          </tr>\r\n		          <tr>\r\n		               <td class=\"legend\">Hip</td>\r\n		               <td class=\"description\">Measure around the fullest part of your hips (approximately 9 inches or 22cm down from waist).</td>\r\n		          </tr>\r\n		    </tbody>     \r\n		</table>\r\n	\r\n	</div>\r\n</div>\r\n','2013-12-23 04:18:13','2014-01-06 22:26:03',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(15,'Glossary','one_column',NULL,NULL,'glossary.html',NULL,'<div class=\"page-title\">\r\n<h1>Glossary</h1>\r\n</div>\r\n\r\n<h2>General Terms</h2>\r\n<table class=\"table table-bordered glossary\">\r\n     <thead>\r\n          <tr>\r\n               <th class=\"term\">term</th>\r\n               <th class=\"definition\">definition</th>\r\n          </tr>\r\n     </thead>\r\n     <tbody>\r\n          <tr>\r\n               <td class=\"term\">A-Line</td>\r\n               <td class=\"definition\">A dress or skirt silhouette that is narrower at the top, flaring gently out to make an \'A\' shape silhouette. Flattering on most figures in particular pear shapes.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Asymmetric</td>\r\n               <td class=\"definition\">An asymmetric design is one that is not symmetric (or the same) on both sides. For example a hem that is longer on one side than the other.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Breton stripe</td>\r\n               <td class=\"definition\">Breton stripes consist of a thin blue horizontal stripes on a white background.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Cableknit</td>\r\n               <td class=\"definition\">A knitting technique that produces a twisted, ropelike design.  Commonly seen on jumpers, cardigans and scarves.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Cap sleeve</td>\r\n               <td class=\"definition\">A cap sleeve is a short sleeve that sits over the edge of the shoulder without extending along the underside of the arm. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Crochet</td>\r\n               <td class=\"definition\">A decorative knitting technique created by looping and knotting yarns with a special needle.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Embroidery</td>\r\n               <td class=\"definition\">Method of decorating fabric with designs stitched in coloured thread or yarn.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Geometric</td>\r\n               <td class=\"definition\">Designs based on simple shapes such as the circle, square, triangle and trapezoid.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Gingham</td>\r\n               <td class=\"definition\">Fabric with an even checked pattern. Colour runs both in horizontal and vertical lines to create small squares. Usually a light weight cotton fabric but is not limited to this. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Inseam</td>\r\n               <td class=\"definition\">The seam in a trouser/pants or short that runs from the crotch to the hem. Usually, leg length is measured based on the inseam. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Knife-pleat</td>\r\n               <td class=\"definition\">A type of pleat that is folded back on itself with a 3:1 ratio. For example a 1 inch pleat has 3 inches of fabric folded to sit as 1 inch wide. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Pleats</td>\r\n               <td class=\"definition\">A section of fabric that is folded back on itself and pressed in place. There are various types of pleats. See knife pleat for one example.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Quilted</td>\r\n               <td class=\"definition\">Padding technique enclosing a layer of wadding between two pieces of fabric, held in place by sewing a diamond pattern over fabrics.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Ribbing</td>\r\n               <td class=\"definition\">A knit pattern that produces vertical stripes of stockinette stitch alternating with vertical stripes of reverse stockinette stitch.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Tapered leg</td>\r\n               <td class=\"definition\">Pant legs that become progressively narrower toward the ankle. Tapered Leg has a relaxed fit through the thigh and knee and tapered from the knee to the ankle.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Tartan</td>\r\n               <td class=\"definition\">A repeated multicolored, square pattern in wool cloth created from the coloured threads used to weave it.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">U-shaped neckline</td>\r\n               <td class=\"definition\">A deeply scooped neckline shaped like letter \"U\", where it derived its name or shaped like horse shoe.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Weave</td>\r\n               <td class=\"definition\">Weaving is a process of interlinking threads or yarns called the wrap and weft created during the cloth production process on a loom. Fabric can either be woven or knitted but weaving can also be used decoratively with patterns and designs. This can be done by hand or machine</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Anorak</td>\r\n               <td class=\"definition\">An anorak is a hooded knee length jacket originally designed for extreme weather conditions. It is now made in various fabrics. Similar to a parka.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Bias</td>\r\n               <td class=\"definition\">When fabric is woven there is a natural grain created by the warp and the weft. When fabric is cut on the bias, it is cut diagonally across this grain. This is done as the fabric hangs and drapes differently when it is cut on the bias. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Raglan sleeve</td>\r\n               <td class=\"definition\">A raglan sleeve begins at the neck and has a long, slanting seam line from the neck to the armhole, giving the garment a relatively undefined shoulder</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Dropped shoulder</td>\r\n               <td class=\"definition\">A dropped shoulder is a style where the sleeve seam sits on the upper arm rather than on the shoulder. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Facing</td>\r\n               <td class=\"definition\">A piece of material that is sewn to finish off an edge neatly- for example a neckline or sleeve hem. This piece is sewn to the edge and then folded around to the back so the neckline is neat and tidy. This can also have lining attached to it.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Cross stitch</td>\r\n               <td class=\"definition\">Cross stitch is used to finish a hem or tack facings. The thread catches a thread or two on the hem, then on the garment,  crossing itself on each half stitch. Creates a flexible hem with some give</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Yoke</td>\r\n               <td class=\"definition\">A yoke is a shaped piece in a garment that aids the fit of the garment. For example, a yoke can be used in the back shoulder region of a garment for a good fit across the shoulders and then below, the back of the garment is loose and flowy</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Placket</td>\r\n               <td class=\"definition\">A placket is a small section of re-enforced fabric that is sewn to the garment along with closures. For example, a dress may have a placket down the front of the dress with buttons. This allows the dress to close neatly and securely</td>\r\n          </tr>\r\n</table>\r\n\r\n\r\n\r\n<h2>Fabrics / Yarns</h2>\r\n<table class=\"table table-bordered glossary\">\r\n     <thead>\r\n          <tr>\r\n               <th class=\"term\">term</th>\r\n               <th class=\"definition\">definition</th>\r\n          </tr>\r\n     </thead>\r\n     <tbody>\r\n          <tr>\r\n               <td class=\"term\">silk crepe</td>\r\n               <td class=\"definition\">Silk crepe is refined silk fibres that are twisted to give a slightly crinkled sheen texture. It can be found in a variety of different weights.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Silk georgette</td>\r\n               <td class=\"definition\">Silk georgette is a fine, sheer  and soft fabric made with twisted silk yarns. It is commonly used for blouses.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Silk dupion</td>\r\n               <td class=\"definition\">A crisp textured fabric woven from silk yarn and has a subtle sheen. It is used for its ability to keep its shape and crisp finish. Silk dupion naturally has bumps or \'slub\'s as a result of fibre bonding. Indian silk dupion usually is more slubby than italian silk dupion. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Cotton poplin</td>\r\n               <td class=\"definition\">A tightly woven high-quality fabric in a plain, flat weave. It is very durable and can be various weights. Commonly used for shirts </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Cotton muslin</td>\r\n               <td class=\"definition\">Cotton muslin is a loosely-woven cotton fabric. It is used for its lightness, softness and its ability to breathe.  </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Cotton lawn</td>\r\n               <td class=\"definition\">A very light weight, plain weave cotton fabric. It is a semi-transparent fabric that is used for its softness and breathability. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">High count cotton</td>\r\n               <td class=\"definition\">This is the thread count per square inch of cotton. The finer the thread woven together, the softer and finer the fabric. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Acetate</td>\r\n               <td class=\"definition\">A generic term for man-made fiber from cellulose acetate. Acetate yarn or thread has a silk like appearance and resists wrinkles. Commonly used for lining. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Viscose </td>\r\n               <td class=\"definition\">Viscose (or rayon) is a semi synthetic fabric. It is manufactured with cellulose from bamboo, plant or tree pulp. It is soft and cool, great for warmer weathers. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Linen</td>\r\n               <td class=\"definition\">Very strong, high luster yarn made from flax that is extremely cool and breathable.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Rayon crepe</td>\r\n               <td class=\"definition\">A natural fiber created from wood pulp, twisted to give a slight crinkle effect. It usually has good drape and is soft.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Organic cotton jersey</td>\r\n               <td class=\"definition\">Organic cotton jersey is made from the soft fibres that grow around the seeds of the cotton plant. The fibres spun into yarn and knitted so that the material has length-wise ribs on the right side, and cross-wise ribs on the wrong side. It has the flexibility and stretch of knit.  </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Polyester </td>\r\n               <td class=\"definition\">A synthetic fibre that is used for its strength, durability and quick drying. Garments can be made from 100% polyester fabric or can be blended with other fibres such as cotton. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Cupro</td>\r\n               <td class=\"definition\">A regenerated cellulose fiber derived from cotton linter (the ultrafine, silky fibers that stick to the seeds of the cotton plant after its been ginned) that has been dissolved in a solution of ammonia and copper oxide. It is similar to rayon, but breathes and regulates body temperature like cotton. Often used as a silk substitute as it can be machine washed and dried</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Lyocell (also known as tencel)</td>\r\n               <td class=\"definition\">Lyocell is created from wood pulp using recycled solvents, It is very soft fabric that is resistant to wrinkles and has great drape. Can also be referred to as tencel- this is a trademark name for this fabric coming from the Lenzing AG group </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Spandex</td>\r\n               <td class=\"definition\">Spandex is a man made fibre. It is lightweight and extremely elastic, possessing the unique ability to stretch up to 600% and then return to its original shape. It is often used for swimwear and commonly added to fabrics like denim to give it stretch.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Wool</td>\r\n               <td class=\"definition\">Wool is a natural fibre shorn from sheep. It is spun to create a yarn which can then be knitted and can be dyed various colours. It is very warm and can be mixed with other fibres such as angora or mohair.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Acrylic</td>\r\n               <td class=\"definition\">Synthetic fibre made from a polymer by combining coal, air, water, petroleum and limestone.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Alpaca </td>\r\n               <td class=\"definition\">Alpaca fleece is harvested from an alpaca. It is a silky and soft fibre that can be light or heavyweight depending on how it is spun. Alpaca wool is warmer than sheep\'s wool and is hypoallergenic. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Angora</td>\r\n               <td class=\"definition\">Angora wool comes from the angora rabbit. It is used for its soft, silky texture and its distinct fluffiness (known as a halo). The rabbit is either brushed or can be shorn to collect the fibres. It is then spun into a yarn. Commonly garments have a blend of angora and wool as pure angora can felt easily and is not naturally elastic. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Australian merino</td>\r\n               <td class=\"definition\">Merino wool is sourced from the merino sheep. It is a fine and soft natural fibre that is known for its quality, warmth and breathability making it perfect for both hot and cold weather.  80% of the worlds merino is produced in Australia and is regarded by many as the finest and softest Merino in the world. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Mohair </td>\r\n               <td class=\"definition\">Mohair comes from the angora goat. It is a very silky fibre known for its high luster and sheen. It is has insulating properties to keep you warm during winter while its moisture wicking properties keep you cool in warmer weather. It is considered a luxury fibre like Angora and cashmere. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Lambswool</td>\r\n               <td class=\"definition\">Lambswool is shorn from sheep before they reach the age of 7 months. It is soft, slippery, resilient and smooth and has superior spinning properties. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Down </td>\r\n               <td class=\"definition\">Down is soft fine filament that is found in the undercoating of waterfowls. There are various types including duck down, goose down and swan down. It is a light, fluffy filament that is used for its insulating properties. It is commonly used in garments like jackets and vests. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Feather</td>\r\n               <td class=\"definition\">Feathers are the individual horny structures which form the outer body covering of birds and consist of a quill point, quill shaft, vanes and usually an aftershaft. Feathers share the same properties as down, however, since they are two-dimensional structures, they do not trap air as efficiently and are, therefore, generally not as good insulators</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Lamb nappa</td>\r\n               <td class=\"definition\">Lamb nappa is full grain leather from the skin of a lamb. It is not split and tanned with salts of chromium or aluminum sulphate. It extremely supple and soft. The leather can be dyed various colours. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Cow hide (also known as \'pony skin\')</td>\r\n               <td class=\"definition\">This comes from the hide from a cow. It is a natural product and unlike leather, still have their natural hair. Some hides have the natural colourings of cows and some are dyed.  This is can also be referred to \'pony skin.\'</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Suede </td>\r\n               <td class=\"definition\">Suede is a leather finished with a fine velvet-like nap, usually on the flesh side of the skin or hide, produced by abrasive action. It has a very soft feel and can be used for garments and accessories. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Corduroy </td>\r\n               <td class=\"definition\">Corduroy is a textile composed of twisted fibers that, when woven, lie parallel to one another to form the cloth\'s distinct pattern, a \"cord.\" It is a very durable fabric and is available in different sizes-see babycord</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Babycord </td>\r\n               <td class=\"definition\">Babycord is a type of corduroy-see definition. The \'cord\' rows are thinner in babycord. It is generally softer than traditional cord. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Chambray</td>\r\n               <td class=\"definition\">Chambray can be made from cotton or synthetic fibres and uses a white weft and indigo warp to create a fabric that looks similar to denim, yet is much lighter.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Denim</td>\r\n               <td class=\"definition\">A type of fabric material comprised of blue cotton warp and white cotton filling. It is a very durable fabric and was traditionally used for work wear. Denim is traditionally dyed with indigo but can be dyed any colour.</td>\r\n          </tr>\r\n</table>\r\n\r\n\r\n\r\n<h2>Fabric Constructions</h2>\r\n<table class=\"table table-bordered glossary\">\r\n     <thead>\r\n          <tr>\r\n               <th class=\"term\">term</th>\r\n               <th class=\"definition\">definition</th>\r\n          </tr>\r\n     </thead>\r\n     <tbody>\r\n          <tr>\r\n               <td class=\"term\">Ikat </td>\r\n               <td class=\"definition\">Ikat fabric is made with a specific weaving technique in which the warp or weft fibres are dyed prior to weaving. When the fabric is woven, the patterns become visible. This is unlike other fabrics which are woven and then dyed with patterns or prints after.  </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Brocade </td>\r\n               <td class=\"definition\">Brocade is a thick, heavy fabric made with a Jacquard loom and a satin weave, most often featuring a raised floral pattern. Brocade is typically made from silk, rayon or nylon, and can have an Oriental look. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Burn out</td>\r\n               <td class=\"definition\">Burnout is when fabrics  have had some of the fibres dissolved through a chemical process, creating a semi-transparent pattern against more solidly woven fabric.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Seersucker </td>\r\n               <td class=\"definition\">Seersucker is a fabric distinguishable by its crepe-like, crinkled stripes, which are made by weaving some of the warp threads slack and others tight. This fabric is traditionally cotton, but can be made from nylon, silk and other yarns, and is typically worn in the summer.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Loopback </td>\r\n               <td class=\"definition\">Loopback fabric is often used for sweatshirts. It has a smooth knit jersey on the front and small loops on the back that are often brushed.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Jacquard</td>\r\n               <td class=\"definition\">A versatile weaving method that allows a wrap thread to be raised independently of the other wrap threads.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Intarsia</td>\r\n               <td class=\"definition\">Intarsia is a flat knit, usually with decorative colour motifs knitted into a solid colour. The pattern is visible on the right and wrong side. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Herringbone</td>\r\n               <td class=\"definition\">Herringbone has a distinctive V-shape/Zig zag weaving pattern. It is a popular choice of cloth for suits and outerwear.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Twill </td>\r\n               <td class=\"definition\">A fabric that shows a distinct woven diagonal wale on the face e.g. denim, garbardine, tricotine.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Check </td>\r\n               <td class=\"definition\">Check is a pattern of modified stripes consisting of crossed horizontal and vertical lines forming squares.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Boucle </td>\r\n               <td class=\"definition\">Boucle is a type of yarn, usually three-ply and having one thread looser than the others, that produces a rough-textured cloth.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Pile</td>\r\n               <td class=\"definition\">Pile is the raised surface or nap of a fabric, which is made of upright loops or strands of yarn.[1] Examples of pile textiles are carpets, corduroy and velvet.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Ponte</td>\r\n               <td class=\"definition\">Ponte is a double-knit interlock fabric that has stability and firmness in addition to flexibility and softness, this means it can hold its shape but still maintain comfort and give.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Flannel</td>\r\n               <td class=\"definition\">Flannel is a soft woven fabric. Traditionally made from wool, it can also been made from cotton or synthetic fibres. Flannel is very soft and warm.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Double faced</td>\r\n               <td class=\"definition\">Double faced fabrics are reversible fabrics with two pleasant sides that can look the same on both sides or have different colors or patterns. Unlike double cloth, this cannot be separated into two pieces of cloth.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Indigo dye</td>\r\n               <td class=\"definition\">Indigo dye is an organic or synthetic compound with a distinctive blue colour. It is the blue colour of blue jeans.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Jersey/Reverse Jersey</td>\r\n               <td class=\"definition\">A general term for any knit fabric, the material has length-wise ribs on the right side, and cross-wise ribs on the wrong side. It is crease-resistant, very resilient, and has the flexibility and stretch of knit. Usually made from wool, cotton or silk, but synthetics are often used as well.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Crepe</td>\r\n               <td class=\"definition\">A fine, gauzelike fabric that can be made of synthetic or natural fibers that are twisted to give a slightly crinkled texture. It can be found in a variety of different weights and levels of sheerness</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Hand knit</td>\r\n               <td class=\"definition\">Hand knitting is the process of interlocking loops of wool or other yarn with knitting needles. A pattern is followed to create pieces that are sewn together to make garments.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Computer knit</td>\r\n               <td class=\"definition\">Computer knit uses a machine to produce various types of knitted fabrics. Patterns can be made using mechanical punch cards, or electronic pattern reading devices and computers.</td>\r\n          </tr>\r\n</table>\r\n\r\n\r\n\r\n<h2>Embellishments</h2>\r\n<table class=\"table table-bordered glossary\">\r\n     <thead>\r\n          <tr>\r\n               <th class=\"term\">term</th>\r\n               <th class=\"definition\">definition</th>\r\n          </tr>\r\n     </thead>\r\n     <tbody>\r\n          <tr>\r\n               <td class=\"term\">Screen print</td>\r\n               <td class=\"definition\">Screen printing is a printing technique that uses a woven mesh to support an ink-blocking stencil to receive a desired image. The attached stencil forms open areas of mesh that transfer ink or other printable materials which can be pressed through the mesh as a sharp-edged image onto a substrate. A fill blade or squeegee is moved across the screen stencil, forcing or pumping ink into the mesh openings for transfer by capillary action during the squeegee stroke</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Digital print</td>\r\n               <td class=\"definition\">Digital printing is a printing technique where fabric is fed through a large inkjet printer that uses fabric specific inks to print the designs on to fabric-much like inkjet printers to print on paper. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Discharge print</td>\r\n               <td class=\"definition\">Discharge printing is a method of applying a design to dyed fabric by printing a colour-destroying agent, such as chlorine or hydrosulfite, to bleach out a white or light pattern on the darker coloured ground. In colour-discharge printing, a dye impervious to the bleaching agent is combined with it, producing a coloured design instead of white on the dyed ground</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Block printing</td>\r\n               <td class=\"definition\">Block printing is a printing technique where small blocks are cut out of wood or metal and ink is applied to the fabric with these shapes to create the pattern or print</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Foiling </td>\r\n               <td class=\"definition\">Foiling is the process of screen printing glue to a piece of fabric then ironing on top a sheet of coloured foil paper. Wherever the glue is printed, the foil will stick. The metal layering adds shine, color or designs to the underlying fabric. often found on spandex and stretch fabrics.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Tie dye</td>\r\n               <td class=\"definition\">Tie dyeing is a dyeing technique in which part of the fabric is tied so they will not absorb dye, giving the fabric an irregular  mottled look</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Dip dye</td>\r\n               <td class=\"definition\">A technique of dying an end of a piece of fabric. The piece of fabric is literally dipped into a pot of dye to create a gradient of colour.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Marbling </td>\r\n               <td class=\"definition\">Marbling is a process of floating fabric paints on the surface of a thick cellulose solution. The floating paints are swirled together to create patterns, then placing the piece of fabric on top to transfer the patterns onto fabric.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Appliqu </td>\r\n               <td class=\"definition\">Cutout design that is sewn upon or otherwise applied to another piece of material</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Patchwork </td>\r\n               <td class=\"definition\">Patchwork is a form of needlework that involves sewing together pieces of fabric into a larger design. This can consist of same size shapes or irregular shapes to be pieced together.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Fringing </td>\r\n               <td class=\"definition\">A trim embellishment /decorative border or edging of hanging threads, cords, or strips, often attached to a separate band.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Hand embroidery</td>\r\n               <td class=\"definition\">Detailed needle work, usually raised and created by yarn, silk, thread or embroidery floss by hand</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Machine embroidery</td>\r\n               <td class=\"definition\">Detailed needle work, usually raised and created by yarn, silk, thread or embroidery floss by machine</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Chikan embroidery</td>\r\n               <td class=\"definition\">Chikan embroidery is traditional embroidery from Lucknow in India. It consists specific stitches and is traditionally uses white thread on white fabric although now colour designs are produced as well. </td>\r\n          </tr>\r\n</table>\r\n\r\n\r\n\r\n\r\n<h2>Finishes</h2>\r\n<table class=\"table table-bordered glossary\">\r\n     <thead>\r\n          <tr>\r\n               <th class=\"term\">term</th>\r\n               <th class=\"definition\">definition</th>\r\n          </tr>\r\n     </thead>\r\n     <tbody>\r\n          <tr>\r\n               <td class=\"term\">Sandwash</td>\r\n               <td class=\"definition\">A finishing process in which the fabric is washed with sand or another abrasive material to produce a soft, suede feel.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Downproofing</td>\r\n               <td class=\"definition\">A fabric which resists the penetration of down. The fabric may be closely woven to be downproof by nature or may be cired or coated to make it downproof.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Permanent pleating</td>\r\n               <td class=\"definition\">A fabric treatment in which pleats (see definition) are set using chemicals. This means that the pleats are permanently pressed into the fabric and wont lose their shape through wear or washing unlike pleats pressed with steam etc.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">French seam</td>\r\n               <td class=\"definition\">A french seam is stitched first on the right side and then turned in and stitched on the wrong side so that the raw edges are enclosed in the seam. This creates a very neat and tidy seam and is commonly used on fine or transparent fabrics such as silk georgette. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Jet pocket</td>\r\n               <td class=\"definition\">A jet pocket is a style of pocket that has two fine rectangle shaped pieces on either side of the pocket opening. The pocket bag is hidden. This is similar to the welt pocket- see definition. Commonly used on jackets and back pockets of pants</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Welt pocket</td>\r\n               <td class=\"definition\">A welt pocket is a style of pocket that has one rectangle shaped lip on the pocket opening. The pocket bag is hidden. This is similar to the jet pocket- see definition. Commonly used on jackets and back pockets of pants</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Smocking </td>\r\n               <td class=\"definition\">Smocking is a traditional needlework technique in which fabric is gathered into tight pleats and then stitched with parallel stitches to keep them in place</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Shirring </td>\r\n               <td class=\"definition\">Shirring is a sewing technique in which elastic is sewn to the fabric in rows resulting in a tightly gathered or pleated effect.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Garment dye</td>\r\n               <td class=\"definition\">Garment dying is a process in which the garment is made and then dyed after.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Coverstitch</td>\r\n               <td class=\"definition\">A coverstitch is used to join and finish seams in knitwear and jersey. On the right side of the fabric is one or two straight lines of stitching, but on the wrong side is a looped stitch that finishes the edge. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Twin stitch</td>\r\n               <td class=\"definition\">Twin stitch is a double row of stitching. Used to finish edges, a double needle with two spools of thread, stitch two parallel lines of stitching at once. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Invisible (blind) hem</td>\r\n               <td class=\"definition\">An invisible or blind hem is one in which not visible on the right side of the fabric. This is different to a machine hem in which a straight row of stitching is sewn all around hem.</td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Machine hem</td>\r\n               <td class=\"definition\">Machine hem is one in which a straight row of stitching is sewn by a machine all around hem. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Bind</td>\r\n               <td class=\"definition\">A bind is a small amount of fabric that is folded around raw edges of a seam and neatly sewn down. This is a very neat seam finish. This can also be used decoratively </td>\r\n          </tr>\r\n</table>\r\n\r\n\r\n\r\n\r\n<h2>Trims</h2>\r\n<table class=\"table table-bordered glossary\">\r\n     <thead>\r\n          <tr>\r\n               <th class=\"term\">term</th>\r\n               <th class=\"definition\">definition</th>\r\n          </tr>\r\n     </thead>\r\n     <tbody>\r\n          <tr>\r\n               <td class=\"term\">Shank button</td>\r\n               <td class=\"definition\">A shank button has a small loop on the back of the button that provides a small amount of space between the button and the garment. This means the button does not have holes in the front unlike shankless buttons and the button is sewn on through the shank. </td>\r\n          </tr>\r\n          <tr>\r\n               <td class=\"term\">Herringbone tape</td>\r\n               <td class=\"definition\">A twilled fabric woven in a pattern consisting of rows of short, slanted parallel lines with the direction of the slant alternating row by row.</td>\r\n          </tr>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n','2013-12-23 04:19:00','2013-12-23 04:19:00',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(19,'Press / Media','one_column',NULL,NULL,'press-media.html',NULL,'<div class=\"row\">\r\n	<div class=\"span8\" id=\"pressreleases\">\r\n\r\n		<h2>Hieroglyphics Vs Holographics</h2>\r\n		<p><em>{{config path=\"general/store_information/name\"}} spring 2013</em></p>\r\n		<p>Our print obsession continues at {{config path=\"general/store_information/name\"}} this spring 2013, but this time were going back to the future with HIEROGLYPHICS Vs Holographics.</p>\r\n		<p>Inspired by Egyptian hieroglyphics, {{config path=\"general/store_information/name\"}} s spring 2013 collection gives you hieroglyphic text prints from hand-made woodblock designs found during travels to India, as seen in the golden mustard Cairo dress and teal blue of the Nile print.</p>\r\n		<p>Youve got to fight for a holographic knock out this October, more to come on this later...</p>\r\n		<p>We got down and dirty with denim, and weve been splashing out with bleach and paint to create the Splatter story, an ode to the wild denim days of the 80s. Each piece is individually painted by hand so no two are alike. The shapes follow suite, with looser fit 80s cut jeans and a denim jacket, and the return of the classic denim bib and brace overall.</p>\r\n		<p>The Sun Over Corn print also uses the paintbrush, but is a more constrained placement print on silks and fine coated cotton. Tribal markings in sunset yellow and fluorescent green have been hand painted and screen printed to look like (obviously!!) sunrise over the ancient grain.</p>\r\n		<p>Our geometric eye is strong again this season, in block prints and bold colours. Using the age old traditional Indian method of Ikat dying and weaving, our old friend the triangle is brought back in yet a new form...See the One Direction Ikat dress. And to top it all off, its overdyed pink.</p>\r\n		<p>Colour blocking is well represented in our Cryptic top and dress, splicing up a sweet and spicy mix of pink, tan, teal and mustard in clean, modern lines.</p>\r\n		<p>Shoes are sporting new details like neoprene, cork and sports buckles, while the classic wooden base becomes a little more refined. As always, socks in our favourite colours and patterns to match each and every shoe... And the super sized tote bag is a winner for all the millions of contents required, large and lush to see you through the constant collecting in life that only a good bag can handle.</p>\r\n		<p>{{config path=\"general/store_information/name\"}} s spring 2013 collection starts to arrive in-store and online from 1st August, with new styles arriving weekly.</p>\r\n\r\n		<p>For more information and imagery please contact:</p>\r\n		<small>Natalie OHeare <br><cite title=\"{{config path=\"general/store_information/name\"}} || Public Relations\">{{config path=\"general/store_information/name\"}} || Public Relations</cite></small><br>\r\n		<small><a href=\"mailto:{{config path=\"trans_email/ident_custom1/email\"}}\" title=\"{{config path=\"trans_email/ident_custom1/email\"}}\">{{config path=\"trans_email/ident_custom1/email\"}}</a></small><br>\r\n		<small><a href=\"{{config path=\"web/unsecure/base_url\"}}\" title=\"{{config path=\"general/store_information/name\"}}\">{{config path=\"web/unsecure/base_url\"}}</a>\r\n\r\n\r\n\r\n	</div>\r\n</div>\r\n ','2013-12-23 04:26:00','2013-12-23 04:26:00',1,0,NULL,NULL,NULL,NULL,NULL,NULL,'','',0),(20,'Contact Us','one_column',NULL,NULL,'contact-us',NULL,'<div class=\"contacttable\">\r\n   <h2>Enquires</h2>\r\n		<table>\r\n			<!--<tr>\r\n				<td class=\'header1\'>{{config path=\"trans_email/ident_general/name\"}}:</td>\r\n				<td><a href=\'mailto:{{config path=\"trans_email/ident_general/email\"}}?subject=Contact%20Princess%20Highway%20Online\' target=\"_blank\">{{config path=\"trans_email/ident_general/email\"}}</a></td>\r\n			</tr>-->\r\n			<tr>\r\n				<td class=\'header1\'>{{config path=\"trans_email/ident_sales/name\"}}:</td>\r\n				<td><a href=\'mailto:{{config path=\"trans_email/ident_sales/email\"}}?subject=Contact%20Princess%20Highway%20Online\' target=\"_blank\">{{config path=\"trans_email/ident_sales/email\"}}</a></td>\r\n			</tr>\r\n			<tr>\r\n				<td class=\'header1\'>{{config path=\"trans_email/ident_support/name\"}}:</td>\r\n				<td><a href=\'mailto:{{config path=\"trans_email/ident_support/email\"}}?subject=Retail%20Enquiry\' target=\"_blank\">{{config path=\"trans_email/ident_support/email\"}}</a></td>\r\n			</tr>\r\n			<tr>\r\n				<td class=\'header1\'>{{config path=\"trans_email/ident_custom1/name\"}}:</td>\r\n				<td><a href=\'mailto:{{config path=\"trans_email/ident_custom1/email\"}}?subject=Princess%20Highway%20Recruitment\' target=\"_blank\">{{config path=\"trans_email/ident_custom1/email\"}}</a>